  width : 50px;
}

.prev, .next {
  font-size: 60px;
  cursor: pointer;
  color: rosybrown;
}

.slides-box {
  width: 500px;
  height: 300px;
  overflow:hidden;
}

.slides {
  display:flex;
  position:relative;
  list-style: none;
  left: 0;
  margin: 0;
  padding-left:0px;
  transition: left 1s;
}

.image {
  display:flex;
  width: 500px;
  height: 300px;
  align-items:center;
  justify-content:center;
  font-size: 50px;
}
.slide__nav {
  display:flex;
  width:140px;
  height: 10px;
  position: relative;
  bottom:30px;
  left:50%;
  margin-left:-70px;
}
.bullet {
  width: 20px;
  background-color : rgba(0,0,0,0.2);
  margin-right: 10px;
  cursor:pointer;
