body {
  margin: 0;
}

.wrap {
  display:flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background: #e3e3e3;
}

.loading {
  display:flex;
  margin-top:100px;
  width : 100px;
  height : 30px;
}

@keyframes animate {
  10% {
    padding : 1.5px;
    background-color : #6ad1af;
  }
  60% {
    padding : 0px;
    background-color : gray;
  }
}

.loading div {
  display:flex;
  align-items:center;
  justify-content:center;
  width : 20px;
  height: 20px;
}

.loading div.dot {
  width:8px;
  height:8px;
  background-color: gray;
  border-radius: 50%;
  transition : 1s;
  animation: animate 2s linear infinite;
  animation-delay: calc(0.18s * var(--j));
}
